{extend name="public/container"}{block name="head_top"}<link href="{__ADMIN_PATH}plug/umeditor/themes/default/css/umeditor.css" type="text/css" rel="stylesheet"><link href="{__ADMIN_PATH}module/wechat/news/css/style.css" type="text/css" rel="stylesheet"><link href="{__FRAME_PATH}css/plugins/chosen/chosen.css" rel="stylesheet"><link href="{__ADMIN_PATH}imgup/css/common.css" type="text/css" rel="stylesheet"><link href="{__ADMIN_PATH}imgup/css/index.css" type="text/css" rel="stylesheet"><link href="{__ADMIN_PATH}emoj/css/emojionearea.min.css" type="text/css" rel="stylesheet"><script type="text/javascript" src="{__ADMIN_PATH}plug/umeditor/third-party/jquery.min.js"></script><script type="text/javascript" src="{__ADMIN_PATH}plug/umeditor/third-party/template.min.js"></script><script type="text/javascript" src="{__ADMIN_PATH}imgup/js/imgPlugin.js"></script><script type="text/javascript" src="{__ADMIN_PATH}emoj/js/emojionearea.min.js"></script><script type="text/javascript" charset="utf-8" src="{__ADMIN_PATH}plug/umeditor/umeditor.config.js"></script><script type="text/javascript" charset="utf-8" src="{__ADMIN_PATH}plug/umeditor/umeditor.min.js"></script><script src="{__ADMIN_PATH}frame/js/ajaxfileupload.js"></script><script src="{__ADMIN_PATH}plug/validate/jquery.validate.js"></script><script src="{__FRAME_PATH}js/plugins/chosen/chosen.jquery.js"></script>{/block}{block name="content"}<div class="row" style="width: 100%">   <div class="col-sm-12">       <div class="col-sm-2 panel panel-default news-left">               <div class="panel-heading">素材列表</div>               <div class="panel-body news-box type-all" >                       <input type="hidden" name="new_id" value="{$news.id}" class="new-id">                   </div>               </div>       </div><!-- col-sm-10 panel panel-default news-right -->       <div class="col-sm-12 panel panel-default" >               <div class="panel-heading">素材内容编辑</div>               <div class="panel-body">                   <form class="form-horizontal" id="signupForm">                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">标题</span>                                   <input maxlength="64" placeholder="请在这里输入标题" name="title" class="layui-input" id="title" value="{$news.title}">                                   <input type="hidden" name="id" id="material_id" value="{$news.id}">                               </div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="input-group">                                   <span class="input-group-addon">素材分类</span>                                   <?php if(empty($all)){?>                                      <select data-placeholder="请先添加素材分类" class="chosen-select"  style="width:100%;" tabindex="4" name="type_id">                                   <?php }else{ ?>                                      <select data-placeholder="选择素材分类" class="chosen-select"  style="width:100%;" tabindex="4" name="type_id" {eq name="$select" value="1"}disabled="disabled"{/eq}>                                   <?php }?>                                       {volist name="all" id="vo" key="k"}                                          {if condition="$key eq $cid"}                                          <option value="{$key}" selected hassubinfo="true" >{$vo}</option>                                          {elseif condition="in_array($key,$news['cid'])"}                                          <option value="{$key}" selected hassubinfo="true" >{$vo}</option>                                          {else/}                                          <option value="{$key}" hassubinfo="true" >{$vo}</option>                                        {/if}                                       {/volist}                                   </select></div>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="form-control" style="height:auto">                                   <p class="up-p">素材图片：<span class="up-span">最多可以上传5张图片</span></p>                                   <div class="z_photo upimg-div clear" >                                        {volist name="images" id="vo"}                                           <section class="up-section fl">                                               <span class="up-span"></span>                                               <img class="close-upimg" src="{$url}/public/system/imgup/img/a7.png">                                               <img class="up-img" src="{$url.$vo}">                                               <p class="img-name-p">345.png</p>                                               <input id="taglocation" name="taglocation" value="" type="hidden">                                               <input id="tags" name="tags" value="" type="hidden">                                               <input type="text" style="display:none;" name="tags[]" value="{$vo}">                                           </section>                                        {/volist}                                       <section class="z_file fl">                                           <img src="{__ADMIN_PATH}imgup/img/a11.png" class="add-img">                                           <input type="file" name="file" id="file" class="file" value="" accept="image/jpg,image/jpeg,image/png,image/bmp" multiple />                                       </section>                                   </div>                                   <p class="help-block" style="margin-top:10px;color:#ccc">封面大图片建议尺寸：900像素 * 500像素</p>                               </div>                               <aside class="mask works-mask">                                   <div class="mask-content">                                       <p class="del-p">您确定要删除素材图片吗？</p>                                       <p class="check-p"><span class="del-com wsdel-ok">确定</span><span class="wsdel-no">取消</span></p>                                   </div>                               </aside>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <label style="color:#aaa">素材内容</label>                               <textarea type="text/plain" id="myEditors" style="width:100%;">{$news['content']}</textarea>                           </div>                       </div>                       <div class="form-group">                           <div class="col-md-12">                               <div class="col-md-6">                                   <label style="color:#aaa">是否显示热门</label>                                   <br/>                                   <input type="radio" name="is_hot" class="layui-radio" value="0" {eq name="news['is_hot']" value="0"}checked{/eq}>否                                   <input type="radio" name="is_hot" class="layui-radio" value="1" {eq name="news['is_hot']" value="1"}checked{/eq}>是                               </div>                           </div>                       </div>                       <div class="form-actions">                           <div class="row">                               <div class="col-md-offset-4 col-md-9">                                   <button type="button" class="btn btn-w-m btn-info save_news">保存</button>                               </div>                           </div>                       </div>                   </form>               </div>       </div>   </div></div>{/block}{block name="script"}<script>        $("#file").takungaeImgup({            formData: {                "path": "",                "name": "tags[]"            },            url: '{:Url('material.material/view_upload')}',            success: function(data) {               console.log(data);            },            error: function(err) {                alert(err);            }        });        $("#myEditors").emojioneArea({            autoHideFilters: true        });        //var el = $("#myEditors").emojioneArea();        function getContent() {            var e = $('#myEditors')[0].emojioneArea;            var text = e.getText();            return (text);        }       /* var editor = document.getElementById('myEditor');        editor.style.height = '300px';        //实例化编辑器        var um = UM.getEditor('myEditor',{            toolbar: [                'fullscreen', 'source', 'undo', 'redo','italic','underline','strikethrough','subscript','horizontal','fontfamily','fontsize',                    'emotion','spechars','justifyleft','justifyright','justifycenter','justifyjustify','forecolor','backcolor','lineheight',            ],            autoHeightEnabled: true,            autoFloatEnabled: true        });        /!**        * 获取编辑器内的内容        * *!/        function getContent() {            return (UM.getEditor('myEditor').getContent());        }        function hasContent() {            return (UM.getEditor('myEditor').hasContents());        }*/        function createFrame(title,src,opt){            opt === undefined && (opt = {});            return layer.open({                type: 2,                title:title,                area: [(opt.w || 700)+'px', (opt.h || 650)+'px'],                fixed: false, //不固定                maxmin: true,                moveOut:false,//true  可以拖出窗外  false 只能在窗内拖                anim:5,//出场动画 isOutAnim bool 关闭动画                offset:'auto',//['100px','100px'],//'auto',//初始位置  ['100px','100px'] t[ 上 左]                shade:0,//遮罩                resize:true,//是否允许拉伸                content: src,//内容                move:'.layui-layer-title'            });        }        function changeIMG(index,pic){            $(".image_img").css('background-image',"url("+pic+")");            $(".active").css('background-image',"url("+pic+")");            $('#image_input').val(pic);        };        /**         * 上传图片         * */        $('.upload_span').on('click',function (e) {//                $('.upload').trigger('click');            createFrame('选择图片','{:Url('widget.images/index')}?fodder=image');        })        /**         * 编辑器上传图片         * */        $('.edui-icon-image').on('click',function (e) {//                $('.upload').trigger('click');            createFrame('选择图片','{:Url('widget.images/index')}?fodder=image');        })        /**        * 提交图文        * */        $('.save_news').on('click',function(){            var tags = '';            $("input[name='tags[]']").each(function(i, el) {                tags=tags+$(this).val()+','            });            var image_input = (tags.substring(tags.length-1)==',')?tags.substring(0,tags.length-1):tags;            var list = {};            list.title = $('#title').val();/* 标题 */            list.author = $('#author').val();/* 作者 */            list.image_input = image_input;/* 图片 */            list.content = getContent();/* 内容 */            list.cid = $('.chosen-select').val();            list.is_hot = $("input[name='is_hot']:checked").val();            var Expression = /http(s)?:\/\/([\w-]+\.)+[\w-]+(\/[\w- .\/?%&=]*)?/;            var objExp=new RegExp(Expression);            if(list.title == ''){                $eb.message('error','请输入标题');                return false;            }            if(list.author == ''){                $eb.message('error','请输入作者');                return false;            }            if(list.image_input == ''){                $eb.message('error','请添加图片');                return false;            }            if(list.content == ''){                $eb.message('error','请输入内容');                return false;            }            var id = $("#material_id").val();            if(id != ''){                list.id = id;            }            var data = {};            $.ajax({                url:"{:Url('add_new')}",                data:list,                type:'post',                dataType:'json',                success:function(re){                    if(re.code == 200){                        data[re.data] = list;                        $('.type-all>.active>.new-id').val(re.data);                        $eb.message('success',re.msg);                        setTimeout(function (e) {                            parent.$(".J_iframe:visible")[0].contentWindow.location.reload();                            parent.layer.close(parent.layer.getFrameIndex(window.name));                        },600)                    }else{                        $eb.message('error',re.msg);                    }                }            })        });        $('.article-add ').on('click',function (e) {            var num_div = $('.type-all').children('div').length;            if(num_div > 7){              $eb.message('error','一组图文消息最多可以添加8个');              return false;            }            var url = "/public/system/module/wechat/news/images/image.png";            html = '';            html += '<div class="news-item transition active news-image" style=" margin-bottom: 20px;background-image:url('+url+')">'                html += '<input type="hidden" name="new_id" value="" class="new-id">';                html += '<span class="news-title del-news">x</span>';            html += '</div>';            $(this).siblings().removeClass("active");            $(this).before(html);        })        $(document).on("click",".del-news",function(){            $(this).parent().remove();        })        $(document).ready(function() {            var config = {                ".chosen-select": {},                ".chosen-select-deselect": {allow_single_deselect: true},                ".chosen-select-no-single": {disable_search_threshold: 10},                ".chosen-select-no-results": {no_results_text: "沒有找到你要搜索的分类"},                ".chosen-select-width": {width: "95%"}            };            for (var selector in config) {                $(selector).chosen(config[selector])            }        })        </script>{/block}